{min = min-60;hr = hr+1;}}if (hr < 10) {hr = "0"+hr}if (min < 10) {min = "0"+min}if (segs < 10) {segs = "0"+segs}fin = hr+":"+min+":"+segsreturn fin;}var segundos = <?php echo $seg; ?>; //inicio do cronometrofunction conta() {segundos--;document.getElementById("counter").innerHTML = formatatempo(segundos);if(segundos <= 0){	<?php setcookie('var', '2'); ?>	document.getElementById("counter").innerHTML = "<p>A Caçada terminou, clique <a href='iniciar.php'>aqui</a> para receber sua recompensa.</p>";}}function inicia(){interval = setInterval("conta();",1000);}window.onload = function(){inicia();}</script></head><body>	<p class="status">Nick: <?php echo $nick;?></p>	<p class="status">Level: <?php echo $level;?></p>	<p class="status">XP: <?php echo $xp.'/'.$prox;?></p>	<p class="status">Ataque: <?php echo $ataque;?></p>	<p class="status">Defesa: <?php echo $defesa;?></p>	<p class="status">Agilidade: <?php echo $agilidade;?></p>	<p class="status">Inteligência: <?php echo $inteligencia;?></p>	<p class="status">Sorte: <?php echo $sorte;?></p>	<p class="status">Crítico: <?php echo $critico;?></p>	<p class="status">Col's: <?php echo $col;?></p><span id="counter"><?php printf('%02d:%02d:%02d', $nHoras, $nMinutos, $nSegundos); ?></span><br></body></html><?php }else {  ?>	<script>		function inicia(){			document.cookie="var= 1";			window.location="iniciar.php";		}		function treinar(){			window.location="treinar.php";		}	</script>  <body>  <?php if($xp >= $prox){		$queryInsert1= "UPDATE `dbNgnl`.`tbtempo` SET `level` = level + 1, `xpProxLevel` = xpProxLevel * 1.40 WHERE `tbtempo`.`codTempo` = 1;";//sql		$Inserir1 = $pdo->prepare($queryInsert1);//insert		$Inserir1->execute();		echo "up";	}?>	<p class="status">Nick: <?php echo $nick;?></p>	<p class="status">Level: <?php echo $level;?></p>	<p class="status">XP: <?php echo $xp.'/'.$prox;?></p>	<p class="status">Ataque: <?php echo $ataque;?></p>	<p class="status">Defes